/**
 * jQuery Validate Custom 2.0
 * 
 * Author Joe
 */
jQuery.extend(smart.validate, {
    options : {
        messages : {
            required 	: "Required",
            select	 	: "Required",
            blank		: "No spaces allowed",
            maxlength 	: "Please enter no more than {0} characters",
            minlength 	: "Please enter at least {0} characters",
            bothlength 	: "Please enter a value between {0} and {1} characters long",
            equallength : "Length must be {0}",
            max 	    : "Must be less than or equal to {0}",
            min     	: "Must be greater than or equal to {0}",
            bothvalue   : "Between {0} and {1}",
            equalvalue  : "Value must be {0}",
            email 		: "Please enter a valid email address",
            integer 	: "Please enter an integer",
            double 		: "Please enter a valid number",
            english 	: "Please enter English",
            chinese 	: "Please enter Chinese",
            phone 		: "Please enter a valid phone number",
            mobile 		: "Please enter a valid mobile number",
            qq 			: "Please enter a valid QQ number",
            postcode 	: "Please enter a valid postal code",
            idcard 	    : "Please enter a valid ID card number"
        },
        vstarHtml : "<span style='color : red;'>*</span>"
    },
	// 验证不通过，高亮显示
	highlight : function($o, message) {
		$o.closest('.form-group').removeClass('has-info').addClass('has-error');
		if(message != undefined && message != null){
			$o.closest('.form-group').find('.help-validate').after("<div class='help-block'>" + message + "</div>") 
		}
	},
	// 验证通过，清除高亮显示
	unhighlight : function($o) {
		$o.closest('.form-group').removeClass('has-error');
		$o.closest('.form-group').find('.help-block').remove();
	}
});
